module.exports = async function t(params, context) {
  const { WX_OPENID, WX_APPID, TCB_UUID } = context.cloudbase.getCloudbaseContext();
  let userId = WX_OPENID || TCB_UUID || "fakeuserid";
  const { database } = context;
  // where 参数不能为空
  let result = await context.collection.where({ _openid: userId }).get();

  if (!result.code) {
    return {
      list: result.data || [],
    };
  }
  // 抛出错误, 错误代码为 1
  throw new TCBError(1, `${result.code} - ${result.message}`);
}

module.exports = async function (params, context) {
  let { database } = context
  const {
    WX_OPENID,
    WX_APPID,
    TCB_UUID
  } = context.cloudbase.getCloudbaseContext()

  let userId = WX_OPENID || TCB_UUID

  if(!userId){
    return {
      list: []
    }
  }

  // where 参数不能为空
  const result = await context.collection.where({_openid: userId}).get();
  if (!result.code) {
    return {
      list: result.data || []
    }
  }
  // 抛出错误, 错误代码为 1
  throw new TCBError(1, `${result.code} - ${result.message}`)
}

export default async function (params, context) {
  const result = await context.collection.add({
    ...params,
    createdTime: Date.now()
  });
  return {
    id: result.id
  }
}

module.exports = async function (params, context) {
  const result = await context.collection.where({id: params.id}).get();
  if (!result.code) {
    if (result.data && result.data.length) {
      let data = result.data[0]
      return data
    }
    // 抛出错误, 错误代码为 1
    throw new TCBError(1, `record ${params.id} not exists`);
  }

  throw new TCBError(2, `${result.code} - ${result.message}`)
}

module.exports = async function (params, context) {
  let {id} = params
  const result = await context.collection.doc(params.id).update({
    invitations: context.database.command.inc(1)
  });
  return {
    updated: result.updated,
  }
}

