  let {database} = context;
  let {ids=[]} = params
  // where 参数不能为空
  const result = await context.collection.where({id: database.command.in(ids)}).get();
  if (!result.code) return {list : result.data}
  // 抛出错误, 错误代码为 1
  throw new TCBError(1, `${result.code} - ${result.message}`)
}

module.exports = async function (params= {}, context) {
  // where 参数不能为空
  const result = await context.collection.where({}).get()
  if (!result.code) {
    let list = (result.data || []).map((item) => {
      return {
        text: item.text,
        value: item.id,
      }
    })
    return {
      list: (params.isRecommend ? [{ text: '推荐', value: 'recommend' }] : []).concat(list),
    }
  }
  // 抛出错误, 错误代码为 1
  throw new TCBError(1, `${result.code} - ${result.message}`)
}

module.exports = async function (params, context) {
  if(!params.id){
    return {}
  }
  const result = await context.collection.where({id: params.id}).get();
  if (!result.code) {
    if (result.data && result.data.length) {
      return result.data[0]
    }
    else {
      return {}
    }
  }

  throw new TCBError(2, `${result.code} - ${result.message}`)
}
